@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=Playfair+Display:wght@400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

@property --syrup-hue {
  syntax: "<number>";
  initial-value: 260;
  inherits: true;
}

:root {
  /* Brouwerij Noordergloed Color Scheme */
  --brewery-orange: 25 100% 50%; /* #FF7900 */
  --brewery-brown-light: 18 25% 57%; /* #A77B71 */
  --brewery-brown-dark: 18 35% 35%; /* #7E4438 */
  --brewery-dark: 18 29% 17%; /* #35231F */

  --background: 35 20% 98%; /* Light warm background */
  --foreground: var(--brewery-dark);
  --muted: 35 15% 92%;
  --muted-foreground: var(--brewery-brown-dark);
  --popover: 0 0% 100%;
  --popover-foreground: var(--brewery-dark);
  --card: 0 0% 100%;
  --card-foreground: var(--brewery-dark);
  --border: 35 20% 88%;
  --input: 35 20% 90%;
  --primary: var(--brewery-orange);
  --primary-foreground: 0 0% 100%;
  --secondary: var(--brewery-brown-light);
  --secondary-foreground: 0 0% 100%;
  --accent: var(--brewery-brown-light);
  --accent-foreground: 0 0% 100%;
  --destructive: 0 84.2% 60.2%;
  --destructive-foreground: 60 9.1% 97.8%;
  --ring: var(--brewery-orange);
  --radius: 0.75rem;

  /* Custom brewery colors for gradients */
  --syrup-accent: oklch(65% 0.25 var(--syrup-hue));
  --elderflower-accent: oklch(75% 0.15 30);
  --rose-accent: oklch(70% 0.20 340);
}

.dark {
  --background: var(--brewery-dark);
  --foreground: 35 20% 95%;
  --muted: 18 35% 22%;
  --muted-foreground: var(--brewery-brown-light);
  --popover: var(--brewery-dark);
  --popover-foreground: 35 20% 95%;
  --card: 18 35% 25%;
  --card-foreground: 35 20% 95%;
  --border: 18 35% 25%;
  --input: 18 35% 25%;
  --primary: var(--brewery-orange);
  --primary-foreground: 0 0% 100%;
  --secondary: var(--brewery-brown-dark);
  --secondary-foreground: 35 20% 95%;
  --accent: var(--brewery-brown-dark);
  --accent-foreground: 35 20% 95%;
  --destructive: 0 62.8% 30.6%;
  --destructive-foreground: 0 0% 98%;
  --ring: var(--brewery-orange);
  --radius: 0.75rem;

  --syrup-hue: 25; /* Orange hue for dark mode */
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', sans-serif;
  }

  .font-display {
    font-family: 'Playfair Display', serif;
  }
}

@layer components {
  .parallax-container {
    overflow: hidden;
  }

  .parallax-element {
    transform: translateZ(0);
    will-change: transform;
  }

  .glass-effect {
    backdrop-filter: blur(20px);
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .brewery-gradient {
    background: linear-gradient(135deg, hsl(var(--brewery-orange)) 0%, hsl(var(--brewery-brown-light)) 100%);
  }

  .brewery-text-gradient {
    background: linear-gradient(135deg, hsl(var(--brewery-orange)) 0%, hsl(var(--brewery-brown-dark)) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .brewery-hero-gradient {
    background: linear-gradient(135deg, 
      hsl(var(--brewery-dark)) 0%, 
      hsl(var(--brewery-brown-dark)) 50%, 
      hsl(var(--brewery-brown-light)) 100%
    );
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fadeIn 0.6s ease-out forwards;
  }

  .animate-slide-up {
    animation: slideUp 0.8s ease-out forwards;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

/* Scroll-driven animations for modern browsers */
@supports (animation-timeline: view()) {
  .scroll-animate {
    animation: parallax linear;
    animation-timeline: view();
    animation-range: entry 0% cover 100%;
  }
}

@keyframes parallax {
  to {
    transform: translateY(-50px);
  }
}

/* View transitions for modern browsers */
@supports (view-transition-name: none) {
  .view-transition-hero {
    view-transition-name: hero;
  }
}

