import { useState, useEffect } from "react";
import { useParams } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Star, ShoppingCart, Minus, Plus, ArrowLeft, Package, Truck, Shield } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Product } from "@shared/schema";
import FeaturedProductsSection from "@/components/FeaturedProductsSection";

export default function ProductPage() {
  const { id } = useParams<{ id: string }>();

  // Scroll to top when component mounts
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [id]);
  const [quantity, setQuantity] = useState(1);
  const [deliveryMethod, setDeliveryMethod] = useState<'pickup' | 'delivery'>('pickup');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: products = [] } = useQuery<Product[]>({
    queryKey: ["/api/products"],
    retry: false,
  });

  const product = products.find(p => p.id === parseInt(id || '0'));

  const addToCartMutation = useMutation({
    mutationFn: async (orderData: any) => {
      return await apiRequest("POST", "/api/orders", orderData);
    },
    onSuccess: () => {
      toast({
        title: "Toegevoegd aan winkelwagen!",
        description: `${quantity}x ${product?.name} toegevoegd`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
    },
    onError: (error: any) => {
      toast({
        title: "Fout bij toevoegen",
        description: error.message || "Er ging iets mis bij het toevoegen aan de winkelwagen",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = () => {
    if (!product) return;

    if (product.stock < quantity) {
      toast({
        title: "Onvoldoende voorraad",
        description: `Nog slechts ${product.stock} stuks beschikbaar`,
        variant: "destructive",
      });
      return;
    }

    addToCartMutation.mutate({
      productId: product.id,
      quantity,
      customerName: "Gast", // This would normally come from user session
      customerEmail: "gast@example.com", // This would normally come from user session
      customerPhone: "",
      deliveryMethod,
      totalAmount: `‚Ç¨${(parseFloat(product.price) * quantity).toFixed(2)}`,
      status: "pending"
    });
  };

  if (!product) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 via-amber-50 to-yellow-50 dark:from-orange-950/20 dark:via-amber-950/20 dark:to-yellow-950/20">
        <div className="container mx-auto px-4 py-20 text-center">
          <h1 className="text-4xl font-display font-bold mb-8">Product niet gevonden</h1>
          <Button onClick={() => window.history.back()}>
            <ArrowLeft className="w-4 h-4 mr-2" />
            Terug
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 via-amber-50 to-yellow-50 dark:from-orange-950/20 dark:via-amber-950/20 dark:to-yellow-950/20">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <Button variant="outline" onClick={() => window.history.back()}>
            <ArrowLeft className="w-4 h-4 mr-2" />
            Terug naar webshop
          </Button>
        </div>

        {/* Product Details */}
        <div className="grid lg:grid-cols-2 gap-12 mb-20">
          {/* Left - Product Image */}
          <div className="space-y-6">
            <div className="aspect-square bg-gradient-to-br from-orange-100 to-amber-200 dark:from-orange-900/50 dark:to-amber-900/50 rounded-2xl overflow-hidden">
              {(product.imagePath || product.imageUrl) ? (
                <img 
                  src={product.imagePath || product.imageUrl} 
                  alt={product.name}
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full flex items-center justify-center">
                  <Package className="w-24 h-24 text-orange-600 dark:text-orange-400" />
                </div>
              )}
            </div>

            {/* Product Features */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Shield className="w-5 h-5 mr-2 text-green-600" />
                  Product Kenmerken
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {product.badges && product.badges.map((badge, index) => (
                  <div key={index} className="flex items-center">
                    <Star className="w-4 h-4 text-orange-500 mr-2" />
                    <span className="text-sm">{badge}</span>
                  </div>
                ))}
                <div className="flex items-center">
                  <Star className="w-4 h-4 text-orange-500 mr-2" />
                  <span className="text-sm">Lokaal geproduceerd in Groningen</span>
                </div>
                <div className="flex items-center">
                  <Star className="w-4 h-4 text-orange-500 mr-2" />
                  <span className="text-sm">Hoogste kwaliteit ingredi√´nten</span>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Right - Product Info & Purchase */}
          <div className="space-y-6">
            {/* Product Header */}
            <div>
              <Badge variant="outline" className="mb-2 text-orange-600 border-orange-300">
                {product.category === 'andere' ? 'Andere Producten' : product.category.charAt(0).toUpperCase() + product.category.slice(1)}
              </Badge>
              <h1 className="font-display text-4xl font-bold text-foreground mb-4">
                {product.name}
              </h1>
              <div className="flex items-baseline gap-2 mb-6">
                <span className="text-4xl font-bold text-orange-600">‚Ç¨{product.price}</span>
                <span className="text-lg text-muted-foreground">per stuk</span>
              </div>
            </div>

            {/* Description */}
            <Card>
              <CardContent className="p-6">
                <p className="text-lg leading-relaxed text-muted-foreground">
                  {product.description}
                </p>
              </CardContent>
            </Card>

            {/* Stock Status */}
            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <span className="font-medium">Voorraad Status:</span>
                  {product.stock === 0 ? (
                    <Badge variant="destructive">Uitverkocht</Badge>
                  ) : product.stock <= 5 ? (
                    <Badge variant="destructive">Laatste {product.stock} stuks!</Badge>
                  ) : (
                    <Badge className="bg-green-500 text-white">{product.stock} op voorraad</Badge>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Purchase Options */}
            {product.stock > 0 && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <ShoppingCart className="w-5 h-5 mr-2" />
                    Bestelling
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  {/* Quantity Selector */}
                  <div className="flex items-center space-x-4">
                    <span className="font-medium">Aantal:</span>
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setQuantity(Math.max(1, quantity - 1))}
                        disabled={quantity <= 1}
                      >
                        <Minus className="w-4 h-4" />
                      </Button>
                      <Input
                        type="number"
                        value={quantity}
                        onChange={(e) => setQuantity(Math.max(1, Math.min(product.stock, parseInt(e.target.value) || 1)))}
                        className="w-20 text-center"
                        min={1}
                        max={product.stock}
                      />
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setQuantity(Math.min(product.stock, quantity + 1))}
                        disabled={quantity >= product.stock}
                      >
                        <Plus className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>

                  {/* Delivery Method */}
                  <div className="space-y-2">
                    <span className="font-medium">Bezorging:</span>
                    <Select value={deliveryMethod} onValueChange={(value: 'pickup' | 'delivery') => setDeliveryMethod(value)}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="pickup">
                          <div className="flex items-center">
                            <Package className="w-4 h-4 mr-2" />
                            Ophalen in Groningen
                          </div>
                        </SelectItem>
                        <SelectItem value="delivery">
                          <div className="flex items-center">
                            <Truck className="w-4 h-4 mr-2" />
                            Bezorgen (+‚Ç¨2,50)
                          </div>
                        </SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <Separator />

                  {/* Total Price */}
                  <div className="flex items-center justify-between text-lg font-bold">
                    <span>Totaal:</span>
                    <span className="text-orange-600">
                      ‚Ç¨{((parseFloat(product.price) * quantity) + (deliveryMethod === 'delivery' ? 2.5 : 0)).toFixed(2)}
                    </span>
                  </div>

                  {/* Add to Cart Button */}
                  <Button 
                    onClick={handleAddToCart}
                    disabled={addToCartMutation.isPending}
                    className="w-full brewery-gradient text-white text-lg py-3 hover:scale-105 transition-all duration-300"
                  >
                    {addToCartMutation.isPending ? "Toevoegen..." : "Toevoegen aan Winkelwagen"}
                  </Button>
                </CardContent>
              </Card>
            )}

            {/* Rating */}
            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <span className="font-medium">Klantbeoordeling:</span>
                  <div className="flex">
                    {[...Array(5)].map((_, i) => (
                      <Star key={i} className="w-5 h-5 fill-yellow-400 text-yellow-400" />
                    ))}
                    <span className="ml-2 text-sm text-muted-foreground">(4.8/5)</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>

        {/* Featured Products Section */}
        <FeaturedProductsSection />
      </div>
    </div>
  );
}

client/src/components/product-carousel.t.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ShoppingCart, Star, ArrowRight } from "lucide-react";
import { useState } from "react";
import type { Product } from "@shared/schema";

export function ProductCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const { data: products = [] } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  // Filter featured products
  const featuredProducts = (products as Product[]).filter((product: Product) => product.featured || product.category === "kombucha");

  if (featuredProducts.length === 0) {
    return null;
  }

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % Math.min(featuredProducts.length, 4));
  };

  const prevSlide = () => {
    setCurrentIndex((prev) => (prev - 1 + Math.min(featuredProducts.length, 4)) % Math.min(featuredProducts.length, 4));
  };

  return (
    <section id="featured-products" className="py-20 bg-gradient-to-br from-amber-50 to-orange-50 dark:from-amber-950/20 dark:to-orange-950/20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <div className="text-center mb-12">
          <h2 className="font-display text-3xl font-bold mb-4">
            <span className="brewery-text-gradient">Onze Specialiteiten</span>
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto mb-6">
            Ontdek onze handgemaakte kombucha collectie, gebrouwen met liefde en lokale ingredi√´nten
          </p>
        </div>

        {/* Product Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {featuredProducts.slice(0, 8).map((product: Product) => (
            <div key={product.id} className="w-full">
                  <Card 
                    className="group hover:shadow-2xl transition-all duration-300 hover:-translate-y-2 overflow-hidden border-2 border-orange-200/50 hover:border-orange-300 cursor-pointer"
                    onClick={() => window.location.href = `/product/${product.id}`}
                  >
                    <div className="relative">
                      {/* Product Image */}
                      <div className="aspect-square bg-gradient-to-br from-orange-100 to-amber-200 relative overflow-hidden">
                        {product.imageUrl ? (
                          <img 
                            src={product.imageUrl} 
                            alt={product.name}
                            className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                          />
                        ) : (
                          <div className="w-full h-full flex items-center justify-center">
                            <div className="w-32 h-32 brewery-gradient rounded-full opacity-20"></div>
                          </div>
                        )}

                        {/* Badge */}
                        {product.badges && product.badges.length > 0 && (
                          <Badge className="absolute top-4 left-4 bg-orange-500 text-white">
                            {product.badges[0]}
                          </Badge>
                        )}

                        {/* Stock Indicator */}
                        {product.stock <= 5 && product.stock > 0 && (
                          <Badge variant="destructive" className="absolute top-4 right-4">
                            Laatste {product.stock}!
                          </Badge>
                        )}

                        {/* Star Rating */}
                        <div className="absolute bottom-4 right-4 flex">
                          {Array.from({ length: 5 }, (_, i) => (
                            <Star key={i} className="w-4 h-4 fill-yellow-400 text-yellow-400" />
                          ))}
                        </div>
                      </div>

                      <CardContent className="p-6">
                        <div className="space-y-4">
                          <div>
                            <h3 className="font-display text-2xl font-bold text-foreground mb-2">
                              {product.name}
                            </h3>
                            <p className="text-muted-foreground line-clamp-3 leading-relaxed">
                              {product.description}
                            </p>
                          </div>

                          <div className="flex items-center justify-between">
                            <div className="text-3xl font-bold text-orange-600">
                              ‚Ç¨{parseFloat(product.price.toString()).toFixed(2)}
                            </div>

                            <Button 
                              className="brewery-gradient text-white hover:scale-105 transition-all duration-200 rounded-full"
                              onClick={(e) => {
                                e.stopPropagation();
                                window.location.href = `/product/${product.id}`;
                              }}
                            >
                              <ShoppingCart className="w-4 h-4 mr-2" />
                              Bekijk Product
                            </Button>
                          </div>
                        </div>
                      </CardContent>
                    </div>
                  </Card>
                </div>
          ))}
        </div>

        {/* CTA Section */}
        <div className="text-center mt-16">
          <Button 
            size="lg"
            className="brewery-gradient text-white px-12 py-4 text-xl rounded-full hover:scale-105 transition-all duration-300"
            onClick={() => window.location.href = "/webshop"}
          >
            Bekijk Alle Producten
            <ArrowRight className="w-5 h-5 ml-3" />
          </Button>
        </div>
      </div>
    </section>
  );
}

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";
import {
  ObjectAclPolicy,
  ObjectPermission,
  canAccessObject,
  getObjectAclPolicy,
  setObjectAclPolicy,
} from "./objectAcl";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();

      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `public, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(rawPath: string): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Tries to set the ACL policy for the object entity and return the normalized path.
  async trySetObjectEntityAclPolicy(
    rawPath: string,
    aclPolicy: ObjectAclPolicy
  ): Promise<string> {
    const normalizedPath = this.normalizeObjectEntityPath(rawPath);
    if (!normalizedPath.startsWith("/")) {
      return normalizedPath;
    }

    const objectFile = await this.getObjectEntityFile(normalizedPath);
    await setObjectAclPolicy(objectFile, aclPolicy);
    return normalizedPath;
  }

  // Checks if the user can access the object entity.
  async canAccessObjectEntity({
    userId,
    objectFile,
    requestedPermission,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    return canAccessObject({
      userId,
      objectFile,
      requestedPermission: requestedPermission ?? ObjectPermission.READ,
    });
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}
